// src/App.js
import React, { useState, useEffect } from 'react';
import TaskList from './components/TaskList';
import TaskForm from './components/TaskForm';
import TaskDetails from './components/TaskDetails';
import Auth from './components/Auth';
import NavBar from './components/Navbar';
import EmployeeList from './components/EmployeeList';

function App() {
  const [view, setView] = useState('login');
  const [user, setUser] = useState(null);
  const [selectedTask, setSelectedTask] = useState(null);

  useEffect(() => {
    const saved = localStorage.getItem('user');
    if (saved) {
      setUser(JSON.parse(saved));
      setView('tasks');
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('user');
    setUser(null);
    setView('login');
  };

  return (
    <div className="container">
      <h1>Task Manager</h1>
      {user && <NavBar setView={setView} onLogout={handleLogout} />}

      {!user && <Auth onLogin={setUser} />}
      {user && view === 'tasks' && (
        <TaskList user={user} onEdit={setSelectedTask} setView={setView} />
      )}
      {user && view === 'create' && (
        <TaskForm task={selectedTask} setView={setView} user={user} />
      )}
      {user && view === 'details' && selectedTask && (
        <TaskDetails task={selectedTask} />
      )}
      {user && view === 'employees' && <EmployeeList />}
    </div>
  );
}

export default App;
