// components/TaskDetail.jsx
import React from 'react';
import axios from 'axios';
import './App.css';

const TaskDetail = ({ task, userRole, token, onClose, onUpdated }) => {
  const handleUpdate = async () => {
    await axios.put(`/api/tasks/${task._id}`, task, {
      headers: { Authorization: `Bearer ${token}` },
    });
    onUpdated();
    onClose();
  };

  return (
    <div className="task-detail">
      <h2>{task.title}</h2>
      <p>{task.description}</p>
      <p>Status: {task.status}</p>
      <p>Due Date: {new Date(task.DueDate).toLocaleDateString()}</p>

      {(userRole === 'Admin' || userRole === 'Supervisor') && (
        <>
          <textarea onChange={(e) => task.description = e.target.value}>{task.description}</textarea>
          <select onChange={(e) => task.status = e.target.value} defaultValue={task.status}>
            <option>Pending</option>
            <option>In Progress</option>
            <option>Completed</option>
          </select>
          <button onClick={handleUpdate}>Save Changes</button>
        </>
      )}
      <button onClick={onClose}>Close</button>
    </div>
  );
};

export default TaskDetail;
