import React, { useEffect, useState } from 'react';
import Auth from './components/Auth';
import Navbar from './components/Navbar';
import TaskForm from './components/TaskForm';
import TaskList from './components/TaskList';
import EmployeeList from './components/EmployeeList';
import ProgressBar from './components/ProgressBar';
import API from './services/api';
import './App.css';

export default function App() {
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [user, setUser] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [taskToEdit, setTaskToEdit] = useState(null);
  const [page, setPage] = useState('list');

  const fetchTasks = async () => {
    const res = await API.get('/tasks');
    setTasks(res.data);
  };

  useEffect(() => {
    if (token) {
      API.get('/auth/profile').then(res => setUser(res.data));
      fetchTasks();
    }
  }, [token]);

  const handleDelete = async id => {
    await API.delete(`/tasks/${id}`);
    fetchTasks();
  };

  if (!token) return <Auth setToken={setToken} setUser={setUser} />;

  return (
    <div className="App">
      <Navbar onNavigate={setPage} user={user} setToken={setToken} />
      <ProgressBar tasks={tasks} />
      {page === 'create' && <TaskForm fetchTasks={fetchTasks} taskToEdit={taskToEdit} clearEdit={() => setTaskToEdit(null)} />}
      {page === 'list' && <TaskList tasks={tasks} onEdit={setTaskToEdit} onDelete={handleDelete} />}
      {page === 'employees' && <EmployeeList />}
    </div>
  );
}
