import React, { useEffect, useState } from 'react';
import Auth from './components/Auth';
import Navbar from './components/Navbar';
import TaskForm from './components/TaskForm';
import TaskList from './components/TaskList';
import ProgressBar from './components/ProgressBar';
import API from './services/api';
import './App.css';

export default function App() {
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [user, setUser] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [taskToEdit, setTaskToEdit] = useState(null);
  const [page, setPage] = useState('list');

  const fetchTasks = async () => {
    try {
      const res = await API.get('/tasks');
      setTasks(res.data);
    } catch (err) {
      console.error('Error fetching tasks:', err);
    }
  };

  // ✅ Fetch user + tasks
  useEffect(() => {
    const fetchUserAndTasks = async () => {
      try {
        const profileRes = await API.get('/auth/profile');
        setUser(profileRes.data); // ✅ Must be set
        await fetchTasks();
      } catch (err) {
        console.error('Token expired or invalid, logging out.');
        setToken(null);
        setUser(null);
        localStorage.removeItem('token');
      }
    };

    if (token) fetchUserAndTasks();
  }, [token]);

  const handleDelete = async id => {
    try {
      await API.delete(`/tasks/${id}`);
      fetchTasks();
    } catch (err) {
      console.error('Error deleting task:', err);
    }
  };

  // ✅ Still protect login screen
  if (!token) return <Auth setToken={setToken} setUser={setUser} />;

  return (
    <div className="App">
      <Navbar onNavigate={setPage} user={user} setToken={setToken} />
      <ProgressBar tasks={tasks} />
      {page === 'create' && (
        <TaskForm
          fetchTasks={fetchTasks}
          taskToEdit={taskToEdit}
          clearEdit={() => setTaskToEdit(null)}
        />
      )}
      {page === 'list' && (
        <TaskList
          tasks={tasks}
          onEdit={task => {
            setTaskToEdit(task);
            setPage('create');
          }}
          onDelete={handleDelete}
          user={user}
        />
      )}
    </div>
  );
}
