// App.jsx
import React, { useEffect, useState } from 'react';
import axios from '../services/api';
import TaskList from './components/TaskList';
import TaskForm from './components/TaskForm';
import TaskDetail from './components/TaskDetails';
import Login from './components/Login';
import Register from './components/Register';
import './App.css';

axios.defaults.baseURL = 'http://localhost:5000/api';
axios.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) config.headers.Authorization = `Bearer ${token}`;
  return config;
});

function App() {
  const [view, setView] = useState('TaskForm');
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [user, setUser] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [selectedTask, setSelectedTask] = useState(null);
  const [search, setSearch] = useState('');
  const [filter, setFilter] = useState('');

  useEffect(() => {
    if (token) {
      axios.get('/auth/me').then(res => {
        setUser(res.data);
        setView('tasks');
      });
    }
  }, [token]);

  const fetchTasks = async () => {
    const res = await axios.get('/tasks');
    setTasks(res.data);
  };

  useEffect(() => {
    if (view === 'tasks') fetchTasks();
  }, [view]);

  const filteredTasks = tasks.filter(task =>
    task.title.toLowerCase().includes(search.toLowerCase()) &&
    (filter ? task.status === filter : true)
  );

  const completedCount = tasks.filter(t => t.status === 'Completed').length;
  const completionRate = tasks.length ? (completedCount / tasks.length) * 100 : 0;

  if (view === 'login') return <Login setView={setView} setToken={setToken} />;
  if (view === 'register') return <Register setView={setView} />;

  return (
    <div className="app-container">
      <h1>Task Management Dashboard</h1>

      <div className="controls">
        <input placeholder="Search by title..." value={search} onChange={(e) => setSearch(e.target.value)} />
        <select onChange={(e) => setFilter(e.target.value)} value={filter}>
          <option value="">All Status</option>
          <option>Pending</option>
          <option>In Progress</option>
          <option>Completed</option>
        </select>
      </div>

      <div className="progress-bar">
        <div className="progress" style={{ width: `${completionRate}%` }}></div>
        <span>{Math.round(completionRate)}% Completed</span>
      </div>

      {(user?.role === 'Admin') && <TaskForm onTaskAdded={fetchTasks} token={token} />}
      <TaskList
        tasks={filteredTasks}
        userRole={user?.role}
        token={token}
        onTaskUpdated={fetchTasks}
        onViewDetails={setSelectedTask}
      />
      {selectedTask && <TaskDetail task={selectedTask} token={token} userRole={user?.role} onClose={() => setSelectedTask(null)} onUpdated={fetchTasks} />}
    </div>
  );
}

export default App;
